import "./card.css";
import ItemCount from "../ItemCount/ItemCount";

import { useContext } from "react";
import { cartContext } from "../../store/cartContext";

function CardDetail({ id, name, precio, imgurl, descripcion, stock }) {
  const { addToCart } = useContext(cartContext);
  /* remplazar "quantityInCart" por un estado para hacer dinamico el renderizado condicional del itemcount */
  const quantityInCart = 0;

  function handleAdd(count) {
    const itemToCart = { id, name, precio, imgurl, descripcion, stock };
    addToCart(itemToCart, count);
    /* setQuantity(count) */
  }

  return (
    <div className="card">
      <div className="card-img">
        <img src={imgurl} alt="imagen" />
      </div>
      <div className="card-detail">
        <h2>{name}</h2>
        <p>{descripcion}</p>
        <h3>U$D {precio}</h3>
      </div>

      {quantityInCart === 0 ? (
        <ItemCount
          initial={1}
          stock={stock}
          onAdd={handleAdd}
          text={"Finalizar"}
        />
      ) : (
        <a href="/cart">Ir al carrito</a>
      )}
    </div>
  );
}

export default CardDetail;
