import React, { useEffect, useState } from "react";
import ItemDetail from "./ItemDetailContainer.js";
import dataItem from "../../dataItem/dataItem.js";
import itemsData from "../../data/data.js";

function getItem(){
    return new Promise((resolve) => {
        setTimeout(()=> resolve(itemsData), 1000);
    });
};



export default function ItemDetailContain() {
const [ data, setdata] = useState([]);

useEffect(
    () => {
        getItem().then((respuesta) => {
            setdata(respuesta);
        });
    },
    []);


return ( <div>
        <h2>Detalles del Producto</h2>
        <p>{data.name}</p>
        <h6>{data.precio}</h6>
        <img src={data.imgurl} alt="imagen"></img>
        <p>{data.descripcion}</p>
        <h6>{data.stock}</h6>
        <hr/>
</div>
)
};
