import React, { useEffect, useState } from "react";
import ItemDetail from "./ItemDetailContainer.js";
import dataItem from "../../dataItem/dataItem.js";
import itemsData from "../../data/data.js";

function getProduct(props){
    const itemsData = [
        {
            id: 1,
            name: "Boston Celtics",
            precio: 122,
            imgurl: "https://picsum.photos/200/300",
            descripcion: "Jersey de equipo NBA Boston Celtics año 2017 del Campeonato Copa Del Rey",
            stock: 7,
            },]
};



export default function ItemDetailContain() {
const [ data, setdata] = useState([]);

useEffect(
    () => {
        getProduct().then((respuesta) => {
            setdata(respuesta);
        });
    },
    []);


return ( <div>
        <h2>Detalles del Producto</h2>
        <p>{props.name}</p>
        <h6>{props.precio}</h6>
        <img src={props.imgurl} alt="imagen"></img>
        <p>{data.descripcion}</p>
        <h6>{data.stock}</h6>
        <hr/>
</div>
)
};
