import React, { useState } from 'react';

function ItemCount( { initial, stock} ) {
    
    console.log(initial, stock);
    
    const [count, setClicks] = useState(1);
    
    const handleIncrement= () => {
        setClicks(count + 1);
    }
    
    const handleDecrement= () => {
        setClicks(count - 1);
    }
    
    return(
    <>
        <h3>AÃ±adir al carrtio</h3>
        <button disabled={count <= 1} onClick={ handleDecrement }>-</button>
        <h3>Productos: {count}</h3>
        <button disabled={count >= 5} onClick={ handleIncrement }>+</button>
    </>
    )
}


export default ItemCount;
